Img** csv_to_imgs_from_C(int number_of_imgs) {
	Img** imgs = cortos_bget(number_of_imgs * sizeof(Img*));

	int i = 0;
	while (i < number_of_imgs){
		imgs[i] = cortos_bget(sizeof(Img));
		int j = 0;
		char* token = strtok(img_list[i], ",");
		imgs[i]->img_data = matrix_create(28, 28);
		while (token != NULL) {
			if (j == 0) {
				imgs[i]->label = atoi(token);
				cortos_printf("image label %d \n", imgs[i]->label);
			} else {
				imgs[i]->img_data->entries[(j-1) / 28][(j-1) % 28] = atoi(token) / 256.0;
				//if(i == 1) {cortos_printf("hi index %d \n", j);}
			}
			token = strtok(NULL, ",");
			j++;
		}
		cortos_printf("first element of j = %d \n", i );
		//cortos_printf("last element of img %f \n", imgs[i]->img_data->entries[(j-2)/28][(j-2) % 28]);
		i++;
	}
	//img_print(imgs[0]);
	return imgs;
}

void img_free(Img* img) {
	matrix_free(img->img_data);
	cortos_brel(img);
	//img = NULL;
}

void imgs_free(Img** imgs, int n) {
	int i ;
	for (i = 0; i < n; i++) {
		img_free(imgs[i]);
	}
	cortos_brel(imgs);
	//imgs = NULL;
}